import py4godot.classes.Button as py4godot_button 
from py4godot.wrappers.wrappers import CPPCheckBoxWrapper


class CheckBox(py4godot_button.Button):
  r'''
		`CheckBox` allows the user to choose one of only two possible options. It's similar to `CheckButton` in functionality, but it has a different appearance. To follow established UX patterns, it's recommended to use `CheckBox` when toggling it has **no** immediate effect on something. For example, it could be used when toggling it will only do something once a confirmation button is pressed.
		See also `BaseButton` which contains common properties and methods associated with this node.
		When `BaseButton.button_group` specifies a `ButtonGroup`, `CheckBox` changes its appearance to that of a radio button and uses the various `radio_*` theme properties.
	'''

  @staticmethod
  def constructor():
    class_ = CheckBox.construct_without_init()
    class_._ptr = constructor(209,0, ())
    return class_
  @staticmethod
  def new():
    class_ = CheckBox.construct_without_init()
    class_._ptr = constructor(209,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPCheckBoxWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(209,0, ())


  def generate_wrapper(self):
    return CPPCheckBoxWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = CheckBox.__new__(CheckBox)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'CheckBox'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = CheckBox.construct_without_init()
    cls._ptr = CPPCheckBoxWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = CheckBox.construct_without_init()
    cls._ptr = CPPCheckBoxWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('CheckBox', CheckBox.cast)
register_class('CheckBox', CheckBox)
