class TitlePosition:
  POSITION_TOP = 0
  POSITION_BOTTOM = 1


class FoldableContainer(py4godot_container.Container):
  r'''
		A container that can be expanded/collapsed, with a title that can be filled with controls, such as buttons.
		The title can be positioned at the top or bottom of the container.
		The container can be expanded or collapsed by clicking the title or by pressing `ui_accept` when focused.
		Child control nodes are hidden when the container is collapsed. Ignores non-control children.
		Can allow grouping with other FoldableContainers, check `foldable_group` and `FoldableGroup`.
	'''

  @staticmethod
  def constructor():
    class_ = FoldableContainer.construct_without_init()
    class_._ptr = constructor(335,0, ())
    return class_
  @staticmethod
  def new():
    class_ = FoldableContainer.construct_without_init()
    class_._ptr = constructor(335,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPFoldableContainerWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(335,0, ())


  def generate_wrapper(self):
    return CPPFoldableContainerWrapper()
  def init_signals(self):
    super().init_signals()
    folding_changed_name = utils.py_string_to_string_name("folding_changed")
    self.folding_changed = signals.BuiltinSignal(self, folding_changed_name)

  @staticmethod
  def construct_without_init():
    cls = FoldableContainer.__new__(FoldableContainer)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'FoldableContainer'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = FoldableContainer.construct_without_init()
    cls._ptr = CPPFoldableContainerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = FoldableContainer.construct_without_init()
    cls._ptr = CPPFoldableContainerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls

