import py4godot.utils.functools as  functools
import py4godot.utils.utils as utils
from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.Separator as py4godot_separator 
from py4godot.wrappers.wrappers import CPPHSeparatorWrapper


class HSeparator(py4godot_separator.Separator):
  r'''
		A horizontal separator used for separating other controls that are arranged **vertically**. `HSeparator` is purely visual and normally drawn as a `StyleBoxLine`.
	'''

  @staticmethod
  def constructor():
    class_ = HSeparator.construct_without_init()
    class_._ptr = constructor(394,0, ())
    return class_
  @staticmethod
  def new():
    class_ = HSeparator.construct_without_init()
    class_._ptr = constructor(394,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPHSeparatorWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(394,0, ())


  def generate_wrapper(self):
    return CPPHSeparatorWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = HSeparator.__new__(HSeparator)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'HSeparator'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = HSeparator.construct_without_init()
    cls._ptr = CPPHSeparatorWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = HSeparator.construct_without_init()
    cls._ptr = CPPHSeparatorWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('HSeparator', HSeparator.cast)
register_class('HSeparator', HSeparator)
