from py4godot.wrappers.wrappers import CPPMeshInstance2DWrapper


class MeshInstance2D(py4godot_node2d.Node2D):
  r'''
		Node used for displaying a `Mesh` in 2D. A `MeshInstance2D` can be automatically created from an existing `Sprite2D` via a tool in the editor toolbar. Select the `Sprite2D` node, then choose **Sprite2D > Convert to MeshInstance2D** at the top of the 2D editor viewport.
	'''

  @staticmethod
  def constructor():
    class_ = MeshInstance2D.construct_without_init()
    class_._ptr = constructor(473,0, ())
    return class_
  @staticmethod
  def new():
    class_ = MeshInstance2D.construct_without_init()
    class_._ptr = constructor(473,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPMeshInstance2DWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(473,0, ())


  def generate_wrapper(self):
    return CPPMeshInstance2DWrapper()
  def init_signals(self):
    super().init_signals()
    texture_changed_name = utils.py_string_to_string_name("texture_changed")
    self.texture_changed = signals.BuiltinSignal(self, texture_changed_name)

  @staticmethod
  def construct_without_init():
    cls = MeshInstance2D.__new__(MeshInstance2D)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'MeshInstance2D'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = MeshInstance2D.construct_without_init()
    cls._ptr = CPPMeshInstance2DWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = MeshInstance2D.construct_without_init()
    cls._ptr = CPPMeshInstance2DWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls

  @property
  def mesh(self) -> typing.Any:
    r''''''
    _ret = self. get_mesh()
    return _ret
  @mesh.setter
  def mesh(self,  value:'py4godot_object.Object'):
    self.set_mesh(value)
  @property
  def texture(self) -> typing.Any:
    r''''''
    _ret = self. get_texture()
    return _ret
  @texture.setter
  def texture(self,  value:'py4godot_object.Object'):
    self.set_texture(value)
  @functools.native_method
  def set_mesh(self, mesh:'py4godot_mesh.Mesh'   ) -> None:
    r''''''

    assert(not mesh is None)
    assert isinstance(mesh, get_class('Mesh')), 'mesh must be Mesh'

    self.py__mesh = mesh






    self._ptr.call_with_return(303,tuple([mesh._ptr]))

  @functools.native_method
  def get_mesh(self) -> typing.Any:
    r''''''




    _ret = classes.Object.Object.construct_without_init() #Smart casted to: Mesh
    _ret._ptr = self._ptr.call_with_return(304,tuple([]))

    return smart_cast(_ret) if not _ret._ptr.is_null() else None


  @functools.native_method
  def set_texture(self, texture:'py4godot_texture2d.Texture2D'   ) -> None:
    r''''''

    assert(not texture is None)
    assert isinstance(texture, get_class('Texture2D')), 'texture must be Texture2D'

    self.py__texture = texture






    self._ptr.call_with_return(305,tuple([texture._ptr]))

  @functools.native_method
  def get_texture(self) -> typing.Any:
    r''''''




    _ret = classes.Object.Object.construct_without_init() #Smart casted to: Texture2D
    _ret._ptr = self._ptr.call_with_return(306,tuple([]))

    return smart_cast(_ret) if not _ret._ptr.is_null() else None



register_cast_function('MeshInstance2D', MeshInstance2D.cast)
register_class('MeshInstance2D', MeshInstance2D)
