import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.ResourceImporter as py4godot_resourceimporter 
from py4godot.wrappers.wrappers import CPPResourceImporterOBJWrapper


class ResourceImporterOBJ(py4godot_resourceimporter.ResourceImporter):
  r'''
		Unlike `ResourceImporterScene`, `ResourceImporterOBJ` will import a single `Mesh` resource by default instead of importing a `PackedScene`. This makes it easier to use the `Mesh` resource in nodes that expect direct `Mesh` resources, such as `GridMap`, `GPUParticles3D` or `CPUParticles3D`. Note that it is still possible to save mesh resources from 3D scenes using the **Advanced Import Settings** dialog, regardless of the source format.
		See also `ResourceImporterScene`, which is used for more advanced 3D formats such as glTF.
	'''

  @staticmethod
  def constructor():
    class_ = ResourceImporterOBJ.construct_without_init()
    class_._ptr = constructor(697,0, ())
    return class_
  @staticmethod
  def new():
    class_ = ResourceImporterOBJ.construct_without_init()
    class_._ptr = constructor(697,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPResourceImporterOBJWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(697,0, ())


  def generate_wrapper(self):
    return CPPResourceImporterOBJWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = ResourceImporterOBJ.__new__(ResourceImporterOBJ)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'ResourceImporterOBJ'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = ResourceImporterOBJ.construct_without_init()
    cls._ptr = CPPResourceImporterOBJWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = ResourceImporterOBJ.construct_without_init()
    cls._ptr = CPPResourceImporterOBJWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('ResourceImporterOBJ', ResourceImporterOBJ.cast)
register_class('ResourceImporterOBJ', ResourceImporterOBJ)
