import py4godot.classes.ResourceImporter as py4godot_resourceimporter 
from py4godot.wrappers.wrappers import CPPResourceImporterTextureWrapper


class ResourceImporterTexture(py4godot_resourceimporter.ResourceImporter):
  r'''
		This importer imports `CompressedTexture2D` resources. If you need to process the image in scripts in a more convenient way, use `ResourceImporterImage` instead. See also `ResourceImporterLayeredTexture`.
	'''

  @staticmethod
  def constructor():
    class_ = ResourceImporterTexture.construct_without_init()
    class_._ptr = constructor(702,0, ())
    return class_
  @staticmethod
  def new():
    class_ = ResourceImporterTexture.construct_without_init()
    class_._ptr = constructor(702,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPResourceImporterTextureWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(702,0, ())


  def generate_wrapper(self):
    return CPPResourceImporterTextureWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = ResourceImporterTexture.__new__(ResourceImporterTexture)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'ResourceImporterTexture'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = ResourceImporterTexture.construct_without_init()
    cls._ptr = CPPResourceImporterTextureWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = ResourceImporterTexture.construct_without_init()
    cls._ptr = CPPResourceImporterTextureWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('ResourceImporterTexture', ResourceImporterTexture.cast)
register_class('ResourceImporterTexture', ResourceImporterTexture)
