from py4godot.wrappers.wrappers import CPPStandardMaterial3DWrapper


class StandardMaterial3D(py4godot_basematerial3d.BaseMaterial3D):
  r'''
		`StandardMaterial3D`'s properties are inherited from `BaseMaterial3D`. `StandardMaterial3D` uses separate textures for ambient occlusion, roughness and metallic maps. To use a single ORM map for all 3 textures, use an `ORMMaterial3D` instead.
	'''

  @staticmethod
  def constructor():
    class_ = StandardMaterial3D.construct_without_init()
    class_._ptr = constructor(783,0, ())
    return class_
  @staticmethod
  def new():
    class_ = StandardMaterial3D.construct_without_init()
    class_._ptr = constructor(783,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPStandardMaterial3DWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(783,0, ())


  def generate_wrapper(self):
    return CPPStandardMaterial3DWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = StandardMaterial3D.__new__(StandardMaterial3D)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'StandardMaterial3D'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = StandardMaterial3D.construct_without_init()
    cls._ptr = CPPStandardMaterial3DWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = StandardMaterial3D.construct_without_init()
    cls._ptr = CPPStandardMaterial3DWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('StandardMaterial3D', StandardMaterial3D.cast)
register_class('StandardMaterial3D', StandardMaterial3D)
