# distutils: language=c++
import py4godot.utils.functools as  functools
import py4godot.utils.utils as utils
from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.ScrollBar as py4godot_scrollbar 
from py4godot.wrappers.wrappers import CPPVScrollBarWrapper


class VScrollBar(py4godot_scrollbar.ScrollBar):
  r'''
		A vertical scrollbar, typically used to navigate through content that extends beyond the visible height of a control. It is a `Range`-based control and goes from top (min) to bottom (max). Note that this direction is the opposite of `VSlider`'s.
	'''

  @staticmethod
  def constructor():
    class_ = VScrollBar.construct_without_init()
    class_._ptr = constructor(862,0, ())
    return class_
  @staticmethod
  def new():
    class_ = VScrollBar.construct_without_init()
    class_._ptr = constructor(862,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPVScrollBarWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(862,0, ())


  def generate_wrapper(self):
    return CPPVScrollBarWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = VScrollBar.__new__(VScrollBar)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'VScrollBar'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = VScrollBar.construct_without_init()
    cls._ptr = CPPVScrollBarWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = VScrollBar.construct_without_init()
    cls._ptr = CPPVScrollBarWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('VScrollBar', VScrollBar.cast)
register_class('VScrollBar', VScrollBar)
