import py4godot.utils.functools as  functools
import py4godot.utils.utils as utils
from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.BoxContainer as py4godot_boxcontainer 
from py4godot.wrappers.wrappers import CPPHBoxContainerWrapper


class HBoxContainer(py4godot_boxcontainer.BoxContainer):
  r'''
		A variant of `BoxContainer` that can only arrange its child controls horizontally. Child controls are rearranged automatically when their minimum size changes.
	'''

  @staticmethod
  def constructor():
    class_ = HBoxContainer.construct_without_init()
    class_._ptr = constructor(390,0, ())
    return class_
  @staticmethod
  def new():
    class_ = HBoxContainer.construct_without_init()
    class_._ptr = constructor(390,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPHBoxContainerWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(390,0, ())


  def generate_wrapper(self):
    return CPPHBoxContainerWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = HBoxContainer.__new__(HBoxContainer)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'HBoxContainer'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = HBoxContainer.construct_without_init()
    cls._ptr = CPPHBoxContainerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = HBoxContainer.construct_without_init()
    cls._ptr = CPPHBoxContainerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('HBoxContainer', HBoxContainer.cast)
register_class('HBoxContainer', HBoxContainer)
