import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.Tweener as py4godot_tweener 
from py4godot.wrappers.wrappers import CPPIntervalTweenerWrapper


class IntervalTweener(py4godot_tweener.Tweener):
  r'''
		`IntervalTweener` is used to make delays in a tweening sequence. See `Tween.tween_interval` for more usage information.
		**Note:** `Tween.tween_interval` is the only correct way to create `IntervalTweener`. Any `IntervalTweener` created manually will not function correctly.
	'''

  @staticmethod
  def constructor():
    class_ = IntervalTweener.construct_without_init()
    class_._ptr = constructor(432,0, ())
    return class_
  @staticmethod
  def new():
    class_ = IntervalTweener.construct_without_init()
    class_._ptr = constructor(432,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPIntervalTweenerWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(432,0, ())


  def generate_wrapper(self):
    return CPPIntervalTweenerWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = IntervalTweener.__new__(IntervalTweener)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'IntervalTweener'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = IntervalTweener.construct_without_init()
    cls._ptr = CPPIntervalTweenerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = IntervalTweener.construct_without_init()
    cls._ptr = CPPIntervalTweenerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('IntervalTweener', IntervalTweener.cast)
register_class('IntervalTweener', IntervalTweener)
