import py4godot.classes.Node2D as py4godot_node2d 
from py4godot.wrappers.wrappers import CPPJoint2DWrapper


class Joint2D(py4godot_node2d.Node2D):
  r'''
		Abstract base class for all joints in 2D physics. 2D joints bind together two physics bodies (`node_a` and `node_b`) and apply a constraint.
	'''

  @staticmethod
  def constructor():
    class_ = Joint2D.construct_without_init()
    class_._ptr = constructor(442,0, ())
    return class_
  @staticmethod
  def new():
    class_ = Joint2D.construct_without_init()
    class_._ptr = constructor(442,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPJoint2DWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(442,0, ())


  def generate_wrapper(self):
    return CPPJoint2DWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = Joint2D.__new__(Joint2D)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'Joint2D'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = Joint2D.construct_without_init()
    cls._ptr = CPPJoint2DWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = Joint2D.construct_without_init()
    cls._ptr = CPPJoint2DWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls

  @property
  def node_a(self) -> typing.Union['NodePath']:
    r''''''
    _ret = self. get_node_a()
    return _ret
  @node_a.setter
  def node_a(self,  value:'object'):
    self.set_node_a(value)
  @property
  def node_b(self) -> typing.Union['NodePath']:
    r''''''
    _ret = self. get_node_b()
    return _ret
  @node_b.setter
  def node_b(self,  value:'object'):
    self.set_node_b(value)
  @property
  def bias(self) -> typing.Union[float]:
    r''''''
    _ret = self. get_bias()
    return _ret
  @bias.setter
  def bias(self,  value:'float'):
    self.set_bias(value)
  @property
  def disable_collision(self) -> typing.Union[bool]:
    r''''''
    _ret = self. get_exclude_nodes_from_collision()
    return _ret
  @disable_collision.setter
  def disable_collision(self,  value:'bool'):
    self.set_exclude_nodes_from_collision(value)
  @functools.native_method
  def set_node_a(self, node:'object'   ) -> None:
    r''''''

    assert(not node is None)
    assert isinstance(node, (str, NodePath)), 'node must be str or NodePath'

    self.py__node_a = node



    assert(isinstance(node, (str, NodePath)))
    py_nodepath_node = node if isinstance(node,NodePath) else NodePath.new2(node)


    self._ptr.call_with_return(303,tuple([py_nodepath_node._ptr]))

  @functools.native_method
  def get_node_a(self) -> typing.Union['NodePath']:
    r''''''




    _ret = NodePath.construct_without_init()
    _ret._ptr = self._ptr.call_with_return(304,tuple([]))

    return _ret


  @functools.native_method
  def set_node_b(self, node:'object'   ) -> None:
    r''''''

    assert(not node is None)
    assert isinstance(node, (str, NodePath)), 'node must be str or NodePath'

    self.py__node_b = node



    assert(isinstance(node, (str, NodePath)))
    py_nodepath_node = node if isinstance(node,NodePath) else NodePath.new2(node)


    self._ptr.call_with_return(305,tuple([py_nodepath_node._ptr]))

  @functools.native_method
  def get_node_b(self) -> typing.Union['NodePath']:
    r''''''




    _ret = NodePath.construct_without_init()
    _ret._ptr = self._ptr.call_with_return(306,tuple([]))

    return _ret


  @functools.native_method
  def set_bias(self, bias:'float'   ) -> None:
    r''''''

    assert isinstance(bias, (int, float)), 'bias must be int or float'

    self.py__bias = bias






    self._ptr.call_with_return(307,tuple([bias]))

  @functools.native_method
  def get_bias(self) -> typing.Union[float]:
    r''''''




    _ret = 0
    _ret = self._ptr.call_with_return(308,tuple([]))

    return _ret


  @functools.native_method
  def set_exclude_nodes_from_collision(self, enable:'bool'   ) -> None:
    r''''''

    assert isinstance(enable, bool), 'enable must be bool'

    self.py__disable_collision = enable






    self._ptr.call_with_return(309,tuple([enable]))

  @functools.native_method
  def get_exclude_nodes_from_collision(self) -> typing.Union[bool]:
    r''''''




    _ret = 0
    _ret = self._ptr.call_with_return(310,tuple([]))

    return _ret


  @functools.native_method
  def get_rid(self) -> typing.Union['RID']:
    r'''
				Returns the joint's internal `RID` from the `PhysicsServer2D`.
			'''




    _ret = RID.construct_without_init()
    _ret._ptr = self._ptr.call_with_return(311,tuple([]))
    return _ret



register_cast_function('Joint2D', Joint2D.cast)
register_class('Joint2D', Joint2D)
