import py4godot.classes.BaseMaterial3D as py4godot_basematerial3d 
from py4godot.wrappers.wrappers import CPPORMMaterial3DWrapper


class ORMMaterial3D(py4godot_basematerial3d.BaseMaterial3D):
  r'''
		ORMMaterial3D's properties are inherited from `BaseMaterial3D`. Unlike `StandardMaterial3D`, ORMMaterial3D uses a single texture for ambient occlusion, roughness and metallic maps, known as an ORM texture.
	'''

  @staticmethod
  def constructor():
    class_ = ORMMaterial3D.construct_without_init()
    class_._ptr = constructor(521,0, ())
    return class_
  @staticmethod
  def new():
    class_ = ORMMaterial3D.construct_without_init()
    class_._ptr = constructor(521,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPORMMaterial3DWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(521,0, ())


  def generate_wrapper(self):
    return CPPORMMaterial3DWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = ORMMaterial3D.__new__(ORMMaterial3D)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'ORMMaterial3D'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = ORMMaterial3D.construct_without_init()
    cls._ptr = CPPORMMaterial3DWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = ORMMaterial3D.construct_without_init()
    cls._ptr = CPPORMMaterial3DWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('ORMMaterial3D', ORMMaterial3D.cast)
register_class('ORMMaterial3D', ORMMaterial3D)
