import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.Control as py4godot_control 
from py4godot.wrappers.wrappers import CPPPanelWrapper


class Panel(py4godot_control.Control):
  r'''
		`Panel` is a GUI control that displays a `StyleBox`. See also `PanelContainer`.
	'''

  @staticmethod
  def constructor():
    class_ = Panel.construct_without_init()
    class_._ptr = constructor(573,0, ())
    return class_
  @staticmethod
  def new():
    class_ = Panel.construct_without_init()
    class_._ptr = constructor(573,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPPanelWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(573,0, ())


  def generate_wrapper(self):
    return CPPPanelWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = Panel.__new__(Panel)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'Panel'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = Panel.construct_without_init()
    cls._ptr = CPPPanelWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = Panel.construct_without_init()
    cls._ptr = CPPPanelWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('Panel', Panel.cast)
register_class('Panel', Panel)
