import py4godot.utils.functools as  functools
import py4godot.utils.utils as utils
from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.PlaceholderTextureLayered as py4godot_placeholdertexturelayered 
from py4godot.wrappers.wrappers import CPPPlaceholderCubemapWrapper


class PlaceholderCubemap(py4godot_placeholdertexturelayered.PlaceholderTextureLayered):
  r'''
		This class replaces a `Cubemap` or a `Cubemap`-derived class in 2 conditions:
		- In dedicated server mode, where the image data shouldn't affect game logic. This allows reducing the exported PCK's size significantly.
		- When the `Cubemap`-derived class is missing, for example when using a different engine version.
		**Note:** This class is not intended for rendering or for use in shaders. Operations like calculating UV are not guaranteed to work.
	'''

  @staticmethod
  def constructor():
    class_ = PlaceholderCubemap.construct_without_init()
    class_._ptr = constructor(619,0, ())
    return class_
  @staticmethod
  def new():
    class_ = PlaceholderCubemap.construct_without_init()
    class_._ptr = constructor(619,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPPlaceholderCubemapWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(619,0, ())


  def generate_wrapper(self):
    return CPPPlaceholderCubemapWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = PlaceholderCubemap.__new__(PlaceholderCubemap)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'PlaceholderCubemap'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = PlaceholderCubemap.construct_without_init()
    cls._ptr = CPPPlaceholderCubemapWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = PlaceholderCubemap.construct_without_init()
    cls._ptr = CPPPlaceholderCubemapWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('PlaceholderCubemap', PlaceholderCubemap.cast)
register_class('PlaceholderCubemap', PlaceholderCubemap)
