# distutils: language=c++
import py4godot.utils.functools as  functools
import py4godot.utils.utils as utils
from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.Window as py4godot_window 
from py4godot.wrappers.wrappers import CPPPopupWrapper


class Popup(py4godot_window.Window):
  r'''
		`Popup` is a base class for contextual windows and panels with fixed position. It's a modal by default (see `Window.popup_window`) and provides methods for implementing custom popup behavior.
	'''

  @staticmethod
  def constructor():
    class_ = Popup.construct_without_init()
    class_._ptr = constructor(633,0, ())
    return class_
  @staticmethod
  def new():
    class_ = Popup.construct_without_init()
    class_._ptr = constructor(633,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPPopupWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(633,0, ())


  def generate_wrapper(self):
    return CPPPopupWrapper()
  def init_signals(self):
    super().init_signals()
    popup_hide_name = utils.py_string_to_string_name("popup_hide")
    self.popup_hide = signals.BuiltinSignal(self, popup_hide_name)

  @staticmethod
  def construct_without_init():
    cls = Popup.__new__(Popup)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'Popup'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = Popup.construct_without_init()
    cls._ptr = CPPPopupWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = Popup.construct_without_init()
    cls._ptr = CPPPopupWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('Popup', Popup.cast)
register_class('Popup', Popup)
