import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.PlaneMesh as py4godot_planemesh 
from py4godot.wrappers.wrappers import CPPQuadMeshWrapper


class QuadMesh(py4godot_planemesh.PlaneMesh):
  r'''
		Class representing a square `PrimitiveMesh`. This flat mesh does not have a thickness. By default, this mesh is aligned on the X and Y axes; this rotation is more suited for use with billboarded materials. A `QuadMesh` is equivalent to a `PlaneMesh` except its default `PlaneMesh.orientation` is `constant PlaneMesh.FACE_Z`.
	'''

  @staticmethod
  def constructor():
    class_ = QuadMesh.construct_without_init()
    class_._ptr = constructor(643,0, ())
    return class_
  @staticmethod
  def new():
    class_ = QuadMesh.construct_without_init()
    class_._ptr = constructor(643,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPQuadMeshWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(643,0, ())


  def generate_wrapper(self):
    return CPPQuadMeshWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = QuadMesh.__new__(QuadMesh)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'QuadMesh'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = QuadMesh.construct_without_init()
    cls._ptr = CPPQuadMeshWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = QuadMesh.construct_without_init()
    cls._ptr = CPPQuadMeshWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('QuadMesh', QuadMesh.cast)
register_class('QuadMesh', QuadMesh)
