from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.TextServerExtension as py4godot_textserverextension 
from py4godot.wrappers.wrappers import CPPTextServerDummyWrapper


class TextServerDummy(py4godot_textserverextension.TextServerExtension):
  r'''
		A dummy `TextServer` interface that doesn't do anything. Useful for freeing up memory when rendering text is not needed, as text servers are resource-intensive. It can also be used for performance comparisons in complex GUIs to check the impact of text rendering.
		A dummy text server is always available at the start of a project. Here's how to access it:
		```gdscript
		var dummy_text_server = TextServerManager.find_interface("Dummy")
		if dummy_text_server != null:
			TextServerManager.set_primary_interface(dummy_text_server)
			# If the other text servers are unneeded, they can be removed:
			for i in TextServerManager.get_interface_count():
				var text_server = TextServerManager.get_interface(i)
				if text_server != dummy_text_server:
					TextServerManager.remove_interface(text_server)
		```
		The command line argument `--text-driver Dummy` (case-sensitive) can be used to force the "Dummy" `TextServer` on any project.
	'''

  @staticmethod
  def constructor():
    class_ = TextServerDummy.construct_without_init()
    class_._ptr = constructor(814,0, ())
    return class_
  @staticmethod
  def new():
    class_ = TextServerDummy.construct_without_init()
    class_._ptr = constructor(814,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPTextServerDummyWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(814,0, ())


  def generate_wrapper(self):
    return CPPTextServerDummyWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = TextServerDummy.__new__(TextServerDummy)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'TextServerDummy'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = TextServerDummy.construct_without_init()
    cls._ptr = CPPTextServerDummyWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = TextServerDummy.construct_without_init()
    cls._ptr = CPPTextServerDummyWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('TextServerDummy', TextServerDummy.cast)
register_class('TextServerDummy', TextServerDummy)
