import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.Resource as py4godot_resource 
from py4godot.wrappers.wrappers import CPPTextureWrapper


class Texture(py4godot_resource.Resource):
  r'''
		`Texture` is the base class for all texture types. Common texture types are `Texture2D` and `ImageTexture`. See also `Image`.
	'''

  @staticmethod
  def constructor():
    class_ = Texture.construct_without_init()
    class_._ptr = constructor(817,0, ())
    return class_
  @staticmethod
  def new():
    class_ = Texture.construct_without_init()
    class_._ptr = constructor(817,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPTextureWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(817,0, ())


  def generate_wrapper(self):
    return CPPTextureWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = Texture.__new__(Texture)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'Texture'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = Texture.construct_without_init()
    cls._ptr = CPPTextureWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = Texture.construct_without_init()
    cls._ptr = CPPTextureWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('Texture', Texture.cast)
register_class('Texture', Texture)
