    r''''''

    assert(not texture_rd_rid is None)
    assert isinstance(texture_rd_rid, RID), 'texture_rd_rid must be RID'

    self.py__texture_rd_rid = texture_rd_rid






    self._ptr.call_with_return(93,tuple([texture_rd_rid._ptr]))

