class VisualShaderNodeInput(py4godot_visualshadernode.VisualShaderNode):
  r'''
		Gives access to input variables (built-ins) available for the shader. See the shading reference for the list of available built-ins for each shader type (check `Tutorials` section for link).
	'''

  @staticmethod
  def constructor():
    class_ = VisualShaderNodeInput.construct_without_init()
    class_._ptr = constructor(912,0, ())
    return class_
  @staticmethod
  def new():
    class_ = VisualShaderNodeInput.construct_without_init()
    class_._ptr = constructor(912,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPVisualShaderNodeInputWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(912,0, ())


  def generate_wrapper(self):
    return CPPVisualShaderNodeInputWrapper()
  def init_signals(self):
    super().init_signals()
    input_type_changed_name = utils.py_string_to_string_name("input_type_changed")
    self.input_type_changed = signals.BuiltinSignal(self, input_type_changed_name)

  @staticmethod
  def construct_without_init():
    cls = VisualShaderNodeInput.__new__(VisualShaderNodeInput)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'VisualShaderNodeInput'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = VisualShaderNodeInput.construct_without_init()
    cls._ptr = CPPVisualShaderNodeInputWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = VisualShaderNodeInput.construct_without_init()
    cls._ptr = CPPVisualShaderNodeInputWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls

  @property
  def input_name(self) -> typing.Union['StringName']:
    r''''''
    _ret = self. get_input_name()
    return _ret
  @input_name.setter
  def input_name(self,  value:'object'):
    self.set_input_name(value)
  @functools.native_method
  def set_input_name(self, name:'str'   ) -> None:
    r''''''

    assert(not name is None)
    assert isinstance(name, (str, String)), 'name must be str or String'

    self.py__input_name = name



    assert(isinstance(name, (str, String)))
    py_string_name = name if isinstance(name, StringName) else c_utils.py_string_to_string(name)

    py__string_name = utils.py_string_to_string(name)
    py__string_name.shouldBeDeleted = False


    self._ptr.call_with_return(88,tuple([py__string_name._ptr]))

  @functools.native_method
  def get_input_name(self) -> typing.Union[str]:
    r''''''




    _ret = String.construct_without_init()
    _ret._ptr = self._ptr.call_with_return(89,tuple([]))

    return utils.gd_string_to_py_string(_ret)


  @functools.native_method
  def get_input_real_name(self) -> typing.Union[str]:
    r'''
				Returns a translated name of the current constant in the Godot Shader Language. E.g. `"ALBEDO"` if the `input_name` equal to `"albedo"`.
			'''




    _ret = String.construct_without_init()
    _ret._ptr = self._ptr.call_with_return(90,tuple([]))
    return utils.gd_string_to_py_string(_ret)



register_cast_function('VisualShaderNodeInput', VisualShaderNodeInput.cast)
register_class('VisualShaderNodeInput', VisualShaderNodeInput)
