# distutils: language=c++
import py4godot.utils.functools as  functools
import py4godot.utils.utils as utils
from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
  import py4godot.classes.DictionaryTypedArray as py4godot_dictionarytypedarray 
import py4godot.classes.DictionaryTypedArray as py4godot_dictionarytypedarray 
import py4godot.classes.RefCounted as py4godot_refcounted 
from py4godot.wrappers.wrappers import CPPJavaClassWrapper


class JavaClass(py4godot_refcounted.RefCounted):
  r'''
		Represents a class from the Java Native Interface. It is returned from `JavaClassWrapper.wrap`.
		**Note:** This class only works on Android. On any other platform, this class does nothing.
		**Note:** This class is not to be confused with `JavaScriptObject`.
	'''

  @staticmethod
  def constructor():
    class_ = JavaClass.construct_without_init()
    class_._ptr = constructor(437,0, ())
    return class_
  @staticmethod
  def new():
    class_ = JavaClass.construct_without_init()
    class_._ptr = constructor(437,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPJavaClassWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(437,0, ())


  def generate_wrapper(self):
    return CPPJavaClassWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = JavaClass.__new__(JavaClass)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'JavaClass'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = JavaClass.construct_without_init()
    cls._ptr = CPPJavaClassWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = JavaClass.construct_without_init()
    cls._ptr = CPPJavaClassWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls

  @functools.native_method
  def get_java_class_name(self) -> typing.Union[str]:
    r'''
				Returns the Java class name.
			'''




    _ret = String.construct_without_init()
    _ret._ptr = self._ptr.call_with_return(53,tuple([]))
    return utils.gd_string_to_py_string(_ret)


  @functools.native_method
  def get_java_method_list(self) -> typing.Union['py4godot_dictionarytypedarray.DictionaryTypedArray']:
    r'''
				Returns the object's Java methods and their signatures as an `Array` of dictionaries, in the same format as `Object.get_method_list`.
			'''




    _ret = py4godot_dictionarytypedarray.DictionaryTypedArray.construct_without_init()
    _ret._ptr = self._ptr.call_with_return(54,tuple([]))
    return _ret


  @functools.native_method
  def get_java_parent_class(self) -> typing.Union['typing.Self']:
    r'''
				Returns a `JavaClass` representing the Java parent class of this class.
			'''




    _ret = JavaClass.construct_without_init()
    _ret._ptr = self._ptr.call_with_return(55,tuple([]))
    return smart_cast(_ret) if not _ret._ptr.is_null() else None



register_cast_function('JavaClass', JavaClass.cast)
register_class('JavaClass', JavaClass)
