# distutils: language=c++
import py4godot.utils.functools as  functools
import py4godot.utils.utils as utils
from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.RefCounted as py4godot_refcounted 
from py4godot.wrappers.wrappers import CPPLightmapperWrapper


class Lightmapper(py4godot_refcounted.RefCounted):
  r'''
		This class should be extended by custom lightmapper classes. Lightmappers can then be used with `LightmapGI` to provide fast baked global illumination in 3D.
		Godot contains a built-in GPU-based lightmapper `LightmapperRD` that uses compute shaders, but custom lightmappers can be implemented by C++ modules.
	'''

  @staticmethod
  def constructor():
    class_ = Lightmapper.construct_without_init()
    class_._ptr = constructor(455,0, ())
    return class_
  @staticmethod
  def new():
    class_ = Lightmapper.construct_without_init()
    class_._ptr = constructor(455,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPLightmapperWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(455,0, ())


  def generate_wrapper(self):
    return CPPLightmapperWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = Lightmapper.__new__(Lightmapper)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'Lightmapper'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = Lightmapper.construct_without_init()
    cls._ptr = CPPLightmapperWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = Lightmapper.construct_without_init()
    cls._ptr = CPPLightmapperWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('Lightmapper', Lightmapper.cast)
register_class('Lightmapper', Lightmapper)
