# distutils: language=c++
import py4godot.utils.functools as  functools
import py4godot.utils.utils as utils
from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.Mesh as py4godot_mesh 
from py4godot.wrappers.wrappers import CPPPlaceholderMeshWrapper


class PlaceholderMesh(py4godot_mesh.Mesh):
  r'''
		This class is used when loading a project that uses a `Mesh` subclass in 2 conditions:
		- When running the project exported in dedicated server mode, only the texture's dimensions are kept (as they may be relied upon for gameplay purposes or positioning of other elements). This allows reducing the exported PCK's size significantly.
		- When this subclass is missing due to using a different engine version or build (e.g. modules disabled).
	'''

  @staticmethod
  def constructor():
    class_ = PlaceholderMesh.construct_without_init()
    class_._ptr = constructor(622,0, ())
    return class_
  @staticmethod
  def new():
    class_ = PlaceholderMesh.construct_without_init()
    class_._ptr = constructor(622,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPPlaceholderMeshWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(622,0, ())


  def generate_wrapper(self):
    return CPPPlaceholderMeshWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = PlaceholderMesh.__new__(PlaceholderMesh)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'PlaceholderMesh'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = PlaceholderMesh.construct_without_init()
    cls._ptr = CPPPlaceholderMeshWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = PlaceholderMesh.construct_without_init()
    cls._ptr = CPPPlaceholderMeshWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls

  @property
  def aabb(self) -> typing.Union['AABB']:
    r''''''
    _ret = self. get_aabb()
    return _ret
  @aabb.setter
  def aabb(self,  value:'AABB'):
    self.set_aabb(value)
  @functools.native_method
  def set_aabb(self, aabb:'AABB'   ) -> None:
    r''''''

    assert(not aabb is None)
    assert isinstance(aabb, AABB), 'aabb must be AABB'

    self.py__aabb = aabb






    self._ptr.call_with_return(105,tuple([aabb._ptr]))


register_cast_function('PlaceholderMesh', PlaceholderMesh.cast)
register_class('PlaceholderMesh', PlaceholderMesh)
