import py4godot.classes.RenderData as py4godot_renderdata 
from py4godot.wrappers.wrappers import CPPRenderDataRDWrapper


class RenderDataRD(py4godot_renderdata.RenderData):
  r'''
		This object manages all render data for the rendering device based renderers.
		**Note:** This is an internal rendering server object only exposed for GDExtension plugins.
	'''

  @staticmethod
  def constructor():
    class_ = RenderDataRD.construct_without_init()
    class_._ptr = constructor(675,0, ())
    return class_
  @staticmethod
  def new():
    class_ = RenderDataRD.construct_without_init()
    class_._ptr = constructor(675,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPRenderDataRDWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(675,0, ())


  def generate_wrapper(self):
    return CPPRenderDataRDWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = RenderDataRD.__new__(RenderDataRD)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'RenderDataRD'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = RenderDataRD.construct_without_init()
    cls._ptr = CPPRenderDataRDWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = RenderDataRD.construct_without_init()
    cls._ptr = CPPRenderDataRDWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('RenderDataRD', RenderDataRD.cast)
register_class('RenderDataRD', RenderDataRD)
