import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.ResourceImporter as py4godot_resourceimporter 
from py4godot.wrappers.wrappers import CPPResourceImporterImageWrapper


class ResourceImporterImage(py4godot_resourceimporter.ResourceImporter):
  r'''
		This importer imports `Image` resources, as opposed to `CompressedTexture2D`. If you need to render the image in 2D or 3D, use `ResourceImporterTexture` instead.
	'''

  @staticmethod
  def constructor():
    class_ = ResourceImporterImage.construct_without_init()
    class_._ptr = constructor(693,0, ())
    return class_
  @staticmethod
  def new():
    class_ = ResourceImporterImage.construct_without_init()
    class_._ptr = constructor(693,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPResourceImporterImageWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(693,0, ())


  def generate_wrapper(self):
    return CPPResourceImporterImageWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = ResourceImporterImage.__new__(ResourceImporterImage)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'ResourceImporterImage'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = ResourceImporterImage.construct_without_init()
    cls._ptr = CPPResourceImporterImageWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = ResourceImporterImage.construct_without_init()
    cls._ptr = CPPResourceImporterImageWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('ResourceImporterImage', ResourceImporterImage.cast)
register_class('ResourceImporterImage', ResourceImporterImage)
