import py4godot.utils.functools as  functools
import py4godot.utils.utils as utils
from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.Control as py4godot_control 
from py4godot.wrappers.wrappers import CPPSeparatorWrapper


class Separator(py4godot_control.Control):
  r'''
		Abstract base class for separators, used for separating other controls. `Separator`s are purely visual and normally drawn as a `StyleBoxLine`.
	'''

  @staticmethod
  def constructor():
    class_ = Separator.construct_without_init()
    class_._ptr = constructor(735,0, ())
    return class_
  @staticmethod
  def new():
    class_ = Separator.construct_without_init()
    class_._ptr = constructor(735,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPSeparatorWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(735,0, ())


  def generate_wrapper(self):
    return CPPSeparatorWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = Separator.__new__(Separator)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'Separator'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = Separator.construct_without_init()
    cls._ptr = CPPSeparatorWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = Separator.construct_without_init()
    cls._ptr = CPPSeparatorWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('Separator', Separator.cast)
register_class('Separator', Separator)
