# distutils: language=c++
import py4godot.utils.functools as  functools
import py4godot.utils.utils as utils
from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.RefCounted as py4godot_refcounted 
from py4godot.wrappers.wrappers import CPPTweenerWrapper


class Tweener(py4godot_refcounted.RefCounted):
  r'''
		Tweeners are objects that perform a specific animating task, e.g. interpolating a property or calling a method at a given time. A `Tweener` can't be created manually, you need to use a dedicated method from `Tween`.
	'''

  @staticmethod
  def constructor():
    class_ = Tweener.construct_without_init()
    class_._ptr = constructor(854,0, ())
    return class_
  @staticmethod
  def new():
    class_ = Tweener.construct_without_init()
    class_._ptr = constructor(854,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPTweenerWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(854,0, ())


  def generate_wrapper(self):
    return CPPTweenerWrapper()
  def init_signals(self):
    super().init_signals()
    finished_name = utils.py_string_to_string_name("finished")
    self.finished = signals.BuiltinSignal(self, finished_name)

  @staticmethod
  def construct_without_init():
    cls = Tweener.__new__(Tweener)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'Tweener'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = Tweener.construct_without_init()
    cls._ptr = CPPTweenerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = Tweener.construct_without_init()
    cls._ptr = CPPTweenerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('Tweener', Tweener.cast)
register_class('Tweener', Tweener)
