# distutils: language=c++
import py4godot.utils.functools as  functools
import py4godot.utils.utils as utils
from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.Slider as py4godot_slider 
from py4godot.wrappers.wrappers import CPPVSliderWrapper


class VSlider(py4godot_slider.Slider):
  r'''
		A vertical slider, used to adjust a value by moving a grabber along a vertical axis. It is a `Range`-based control and goes from bottom (min) to top (max). Note that this direction is the opposite of `VScrollBar`'s.
	'''

  @staticmethod
  def constructor():
    class_ = VSlider.construct_without_init()
    class_._ptr = constructor(864,0, ())
    return class_
  @staticmethod
  def new():
    class_ = VSlider.construct_without_init()
    class_._ptr = constructor(864,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPVSliderWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(864,0, ())


  def generate_wrapper(self):
    return CPPVSliderWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = VSlider.__new__(VSlider)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'VSlider'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = VSlider.construct_without_init()
    cls._ptr = CPPVSliderWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = VSlider.construct_without_init()
    cls._ptr = CPPVSliderWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('VSlider', VSlider.cast)
register_class('VSlider', VSlider)
