import py4godot.classes.SplitContainer as py4godot_splitcontainer 
from py4godot.wrappers.wrappers import CPPVSplitContainerWrapper


class VSplitContainer(py4godot_splitcontainer.SplitContainer):
  r'''
		A container that accepts only two child controls, then arranges them vertically and creates a divisor between them. The divisor can be dragged around to change the size relation between the child controls.
	'''

  @staticmethod
  def constructor():
    class_ = VSplitContainer.construct_without_init()
    class_._ptr = constructor(865,0, ())
    return class_
  @staticmethod
  def new():
    class_ = VSplitContainer.construct_without_init()
    class_._ptr = constructor(865,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPVSplitContainerWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(865,0, ())


  def generate_wrapper(self):
    return CPPVSplitContainerWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = VSplitContainer.__new__(VSplitContainer)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'VSplitContainer'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = VSplitContainer.construct_without_init()
    cls._ptr = CPPVSplitContainerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = VSplitContainer.construct_without_init()
    cls._ptr = CPPVSplitContainerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('VSplitContainer', VSplitContainer.cast)
register_class('VSplitContainer', VSplitContainer)
