if typing.TYPE_CHECKING:
  pass
import py4godot.classes.XRPositionalTracker as py4godot_xrpositionaltracker 
from py4godot.wrappers.wrappers import CPPXRControllerTrackerWrapper


class XRControllerTracker(py4godot_xrpositionaltracker.XRPositionalTracker):
  r'''
		An instance of this object represents a controller that is tracked.
		As controllers are turned on and the `XRInterface` detects them, instances of this object are automatically added to this list of active tracking objects accessible through the `XRServer`.
		The `XRController3D` consumes objects of this type and should be used in your project.
	'''

  @staticmethod
  def constructor():
    class_ = XRControllerTracker.construct_without_init()
    class_._ptr = constructor(1016,0, ())
    return class_
  @staticmethod
  def new():
    class_ = XRControllerTracker.construct_without_init()
    class_._ptr = constructor(1016,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPXRControllerTrackerWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(1016,0, ())


  def generate_wrapper(self):
    return CPPXRControllerTrackerWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = XRControllerTracker.__new__(XRControllerTracker)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'XRControllerTracker'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = XRControllerTracker.construct_without_init()
    cls._ptr = CPPXRControllerTrackerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = XRControllerTracker.construct_without_init()
    cls._ptr = CPPXRControllerTrackerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('XRControllerTracker', XRControllerTracker.cast)
register_class('XRControllerTracker', XRControllerTracker)
