# distutils: language=c++
import py4godot.utils.functools as  functools
import py4godot.utils.utils as utils
from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.Tweener as py4godot_tweener 
from py4godot.wrappers.wrappers import CPPCallbackTweenerWrapper


class CallbackTweener(py4godot_tweener.Tweener):
  r'''
		`CallbackTweener` is used to call a method in a tweening sequence. See `Tween.tween_callback` for more usage information.
		The tweener will finish automatically if the callback's target object is freed.
		**Note:** `Tween.tween_callback` is the only correct way to create `CallbackTweener`. Any `CallbackTweener` created manually will not function correctly.
	'''

  @staticmethod
  def constructor():
    class_ = CallbackTweener.construct_without_init()
    class_._ptr = constructor(187,0, ())
    return class_
  @staticmethod
  def new():
    class_ = CallbackTweener.construct_without_init()
    class_._ptr = constructor(187,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPCallbackTweenerWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(187,0, ())


  def generate_wrapper(self):
    return CPPCallbackTweenerWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = CallbackTweener.__new__(CallbackTweener)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'CallbackTweener'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = CallbackTweener.construct_without_init()
    cls._ptr = CPPCallbackTweenerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = CallbackTweener.construct_without_init()
    cls._ptr = CPPCallbackTweenerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls

  @functools.native_method
  def set_delay(self, delay:'float'   ) -> typing.Union['typing.Self']:
    r'''
				Makes the callback call delayed by given time in seconds.
				**Example:** Call `Node.queue_free` after 2 seconds:
				```gdscript
				var tween = get_tree().create_tween()
				tween.tween_callback(queue_free).set_delay(2)
				```
			'''

    assert isinstance(delay, (int, float)), 'delay must be int or float'






    _ret = CallbackTweener.construct_without_init()
    _ret._ptr = self._ptr.call_with_return(53,tuple([delay]))
    return smart_cast(_ret) if not _ret._ptr.is_null() else None



register_cast_function('CallbackTweener', CallbackTweener.cast)
register_class('CallbackTweener', CallbackTweener)
