import py4godot.classes.CompressedTextureLayered as py4godot_compressedtexturelayered 
from py4godot.wrappers.wrappers import CPPCompressedCubemapWrapper


class CompressedCubemap(py4godot_compressedtexturelayered.CompressedTextureLayered):
  r'''
		A cubemap that is loaded from a `.ccube` file. This file format is internal to Godot; it is created by importing other image formats with the import system. `CompressedCubemap` can use one of 4 compression methods:
		- Lossless (WebP or PNG, uncompressed on the GPU)
		- Lossy (WebP, uncompressed on the GPU)
		- VRAM Compressed (compressed on the GPU)
		- VRAM Uncompressed (uncompressed on the GPU)
		- Basis Universal (compressed on the GPU. Lower file sizes than VRAM Compressed, but slower to compress and lower quality than VRAM Compressed)
		Only **VRAM Compressed** actually reduces the memory usage on the GPU. The **Lossless** and **Lossy** compression methods will reduce the required storage on disk, but they will not reduce memory usage on the GPU as the texture is sent to the GPU uncompressed.
		Using **VRAM Compressed** also improves loading times, as VRAM-compressed textures are faster to load compared to textures using lossless or lossy compression. VRAM compression can exhibit noticeable artifacts and is intended to be used for 3D rendering, not 2D.
		See `Cubemap` for a general description of cubemaps.
	'''

  @staticmethod
  def constructor():
    class_ = CompressedCubemap.construct_without_init()
    class_._ptr = constructor(227,0, ())
    return class_
  @staticmethod
  def new():
    class_ = CompressedCubemap.construct_without_init()
    class_._ptr = constructor(227,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPCompressedCubemapWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(227,0, ())


  def generate_wrapper(self):
    return CPPCompressedCubemapWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = CompressedCubemap.__new__(CompressedCubemap)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'CompressedCubemap'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = CompressedCubemap.construct_without_init()
    cls._ptr = CPPCompressedCubemapWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = CompressedCubemap.construct_without_init()
    cls._ptr = CPPCompressedCubemapWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('CompressedCubemap', CompressedCubemap.cast)
register_class('CompressedCubemap', CompressedCubemap)
