import py4godot.utils.functools as  functools
import py4godot.utils.utils as utils
from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.FlowContainer as py4godot_flowcontainer 
from py4godot.wrappers.wrappers import CPPHFlowContainerWrapper


class HFlowContainer(py4godot_flowcontainer.FlowContainer):
  r'''
		A variant of `FlowContainer` that can only arrange its child controls horizontally, wrapping them around at the borders. This is similar to how text in a book wraps around when no more words can fit on a line.
	'''

  @staticmethod
  def constructor():
    class_ = HFlowContainer.construct_without_init()
    class_._ptr = constructor(391,0, ())
    return class_
  @staticmethod
  def new():
    class_ = HFlowContainer.construct_without_init()
    class_._ptr = constructor(391,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPHFlowContainerWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(391,0, ())


  def generate_wrapper(self):
    return CPPHFlowContainerWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = HFlowContainer.__new__(HFlowContainer)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'HFlowContainer'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = HFlowContainer.construct_without_init()
    cls._ptr = CPPHFlowContainerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = HFlowContainer.construct_without_init()
    cls._ptr = CPPHFlowContainerWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('HFlowContainer', HFlowContainer.cast)
register_class('HFlowContainer', HFlowContainer)
