class LoaderFlags:
  FLAG_NONE = 0
  FLAG_FORCE_LINEAR = 1
  FLAG_CONVERT_COLORS = 2


class ImageFormatLoader(py4godot_refcounted.RefCounted):
  r'''
		The engine supports multiple image formats out of the box (PNG, SVG, JPEG, WebP to name a few), but you can choose to implement support for additional image formats by extending `ImageFormatLoaderExtension`.
	'''

  @staticmethod
  def constructor():
    class_ = ImageFormatLoader.construct_without_init()
    class_._ptr = constructor(404,0, ())
    return class_
  @staticmethod
  def new():
    class_ = ImageFormatLoader.construct_without_init()
    class_._ptr = constructor(404,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPImageFormatLoaderWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(404,0, ())


  def generate_wrapper(self):
    return CPPImageFormatLoaderWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = ImageFormatLoader.__new__(ImageFormatLoader)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'ImageFormatLoader'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = ImageFormatLoader.construct_without_init()
    cls._ptr = CPPImageFormatLoaderWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = ImageFormatLoader.construct_without_init()
    cls._ptr = CPPImageFormatLoaderWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('ImageFormatLoader', ImageFormatLoader.cast)
register_class('ImageFormatLoader', ImageFormatLoader)
