# distutils: language=c++
import py4godot.utils.functools as  functools
import py4godot.utils.utils as utils
from py4godot.classes.core import *
from py4godot.utils.smart_cast import smart_cast, register_cast_function, register_class, get_class
from py4godot.utils.CPPWrapper import CPPWrapper, constructor, static_method
import py4godot.utils.utils as c_utils
import typing
import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.Lightmapper as py4godot_lightmapper 
from py4godot.wrappers.wrappers import CPPLightmapperRDWrapper


class LightmapperRD(py4godot_lightmapper.Lightmapper):
  r'''
		LightmapperRD ("RD" stands for `RenderingDevice`) is the built-in GPU-based lightmapper for use with `LightmapGI`. On most dedicated GPUs, it can bake lightmaps much faster than most CPU-based lightmappers. LightmapperRD uses compute shaders to bake lightmaps, so it does not require CUDA or OpenCL libraries to be installed to be usable.
		**Note:** Only usable when using the RenderingDevice backend (Forward+ or Mobile renderers), not Compatibility.
	'''

  @staticmethod
  def constructor():
    class_ = LightmapperRD.construct_without_init()
    class_._ptr = constructor(456,0, ())
    return class_
  @staticmethod
  def new():
    class_ = LightmapperRD.construct_without_init()
    class_._ptr = constructor(456,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPLightmapperRDWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(456,0, ())


  def generate_wrapper(self):
    return CPPLightmapperRDWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = LightmapperRD.__new__(LightmapperRD)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'LightmapperRD'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = LightmapperRD.construct_without_init()
    cls._ptr = CPPLightmapperRDWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = LightmapperRD.construct_without_init()
    cls._ptr = CPPLightmapperRDWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('LightmapperRD', LightmapperRD.cast)
register_class('LightmapperRD', LightmapperRD)
