import py4godot.classes.StyleBox as py4godot_stylebox 
from py4godot.wrappers.wrappers import CPPStyleBoxEmptyWrapper


class StyleBoxEmpty(py4godot_stylebox.StyleBox):
  r'''
		An empty `StyleBox` that can be used to display nothing instead of the default style (e.g. it can "disable" `focus` styles).
	'''

  @staticmethod
  def constructor():
    class_ = StyleBoxEmpty.construct_without_init()
    class_._ptr = constructor(794,0, ())
    return class_
  @staticmethod
  def new():
    class_ = StyleBoxEmpty.construct_without_init()
    class_._ptr = constructor(794,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPStyleBoxEmptyWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(794,0, ())


  def generate_wrapper(self):
    return CPPStyleBoxEmptyWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = StyleBoxEmpty.__new__(StyleBoxEmpty)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'StyleBoxEmpty'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = StyleBoxEmpty.construct_without_init()
    cls._ptr = CPPStyleBoxEmptyWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = StyleBoxEmpty.construct_without_init()
    cls._ptr = CPPStyleBoxEmptyWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('StyleBoxEmpty', StyleBoxEmpty.cast)
register_class('StyleBoxEmpty', StyleBoxEmpty)
