class TextureCubemapRD(py4godot_texturelayeredrd.TextureLayeredRD):
  r'''
		This texture class allows you to use a cubemap texture created directly on the `RenderingDevice` as a texture for materials, meshes, etc.
	'''

  @staticmethod
  def constructor():
    class_ = TextureCubemapRD.construct_without_init()
    class_._ptr = constructor(826,0, ())
    return class_
  @staticmethod
  def new():
    class_ = TextureCubemapRD.construct_without_init()
    class_._ptr = constructor(826,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPTextureCubemapRDWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(826,0, ())


  def generate_wrapper(self):
    return CPPTextureCubemapRDWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = TextureCubemapRD.__new__(TextureCubemapRD)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'TextureCubemapRD'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = TextureCubemapRD.construct_without_init()
    cls._ptr = CPPTextureCubemapRDWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = TextureCubemapRD.construct_without_init()
    cls._ptr = CPPTextureCubemapRDWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('TextureCubemapRD', TextureCubemapRD.cast)
register_class('TextureCubemapRD', TextureCubemapRD)
