class VisualShaderNodeIf(py4godot_visualshadernode.VisualShaderNode):
  r'''
		This visual shader node has six input ports:
		- Port **1** and **2** provide the two floating-point numbers `a` and `b` that will be compared.
		- Port **3** is the tolerance, which allows similar floating-point numbers to be considered equal.
		- Ports **4**, **5**, and **6** are the possible outputs, returned if `a == b`, `a > b`, or `a < b` respectively.
	'''

  @staticmethod
  def constructor():
    class_ = VisualShaderNodeIf.construct_without_init()
    class_._ptr = constructor(911,0, ())
    return class_
  @staticmethod
  def new():
    class_ = VisualShaderNodeIf.construct_without_init()
    class_._ptr = constructor(911,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPVisualShaderNodeIfWrapper()
    if c_utils.shouldCreateObject:
      self._ptr = constructor(911,0, ())


  def generate_wrapper(self):
    return CPPVisualShaderNodeIfWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = VisualShaderNodeIf.__new__(VisualShaderNodeIf)
    cls.shouldBeDeleted = True
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'VisualShaderNodeIf'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = VisualShaderNodeIf.construct_without_init()
    cls._ptr = CPPVisualShaderNodeIfWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = VisualShaderNodeIf.construct_without_init()
    cls._ptr = CPPVisualShaderNodeIfWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('VisualShaderNodeIf', VisualShaderNodeIf.cast)
register_class('VisualShaderNodeIf', VisualShaderNodeIf)
